                ans[depth].push_back(node->val);
                Q.pop();
            }
            depth++;
        }
        for(int i=1;i<depth;i+=2)
            reverse(ans[i]);
        return ans;
    }
    vector<int> reverse(vector<int>& arr)
    {
        int len=arr.size();
        if(len<=1)
            return arr;
        for(int i=0;i<len/2;i++)//reverse
        {
            int temp=arr[i];
            arr[i]=arr[len-i-1];
            arr[len-i-1]=temp;
        }
        return arr;
    }
};
