			}

	int* Latest_Finish = new int[num + 2];
	int Finish_time = Earliset_Finish[num];
	for (int i = 1; i <= num; i++)
		Latest_Finish[i] = Finish_time;
	//******************* Topological Sort *******************

	stk.push(num);//need to optimize...
	stk.push(num - 1);//need to optimize...
	while (!stk.empty())
	{
